%Function assuming sorted array of points as inputfunction y = area_pol(matrix_points_xy)  y = 0; %init counter  for i=1:length(matrix_points_xy(:,1))    %If/else structure to choose correct indexing    if i ~= length(matrix_points_xy(:,1)) && i ~= 1      y = y +  matrix_points_xy(i,2)*(matrix_points_xy(i+1,1) - matrix_points_xy(i-1,1));    elseif i == 1      y = y +  matrix_points_xy(i,2)*(matrix_points_xy(i+1,1) - matrix_points_xy(length(matrix_points_xy(:,1)),1));    else      y = y +  matrix_points_xy(i,2)*(matrix_points_xy(1,1) - matrix_points_xy(i-1,1));    end %ifelse  end %for    y = 0.5*y; %Need to half the summed areaend %fnc